import React, { useState } from 'react';
import axios from 'axios';
import './Signup.css'; // Import the CSS file for styling

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [usernameError, setUsernameError] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [confirmPasswordError, setConfirmPasswordError] = useState('');
  const [message, setMessage] = useState('');
  const [registrationSuccess, setRegistrationSuccess] = useState(false);

  const validateUsername = () => {
    if (!username) {
      setUsernameError('Username is required.');
    } else {
      setUsernameError('');
    }
  };

  const validateEmail = () => {
    if (!email) {
      setEmailError('Email is required.');
    } else if (!email.endsWith('@maersk.com')) {
      setEmailError('Invalid email format. Please use an email ending with "@maersk.com".');
    } else {
      setEmailError('');
    }
  };

  const validatePassword = () => {
    if (!password) {
      setPasswordError('Password is required.');
    } else if (!/^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,12}$/.test(password)) {
      setPasswordError('Password must contain one or more special characters, alphanumeric characters, with a length between 8 and 12.');
    } else {
      setPasswordError('');
    }
  };

  const validateConfirmPassword = () => {
    if (!confirmPassword) {
      setConfirmPasswordError('Confirm Password is required.');
    } else if (confirmPassword !== password) {
      setConfirmPasswordError('Password and Confirm Password do not match.');
    } else {
      setConfirmPasswordError('');
    }
  };

  const checkDuplicate = async () => {
    try {
      const response = await axios.post('http://localhost:5002/api/check-duplicate', {
        username,
        email,
      });

      if (response.data.exists) {
        setMessage('Username or email already exists. Please choose a different one.');
        return true; // Duplicate found
      } else {
        return false; // No duplicate
      }
    } catch (error) {
      console.error('Error checking for duplicate values:', error);
      return true; // Assume duplicate on error
    }
  };

  const handleSignup = async () => {
    // Validate all fields before performing signup
    validateUsername();
    validateEmail();
    validatePassword();
    validateConfirmPassword();

    // Check for duplicate values
    if (await checkDuplicate()) {
      return; // Stop signup process if duplicate values are found
    }

    // Check if any validation errors exist
    if (usernameError || emailError || passwordError || confirmPasswordError) {
      return;
    }

    try {
      // Perform signup if validations pass
      const response = await axios.post('http://localhost:5002/api/signup', {
        username,
        email,
        password,
      });

      setMessage(response.data.message);

      // Update state to indicate successful registration
      setRegistrationSuccess(true);
    } catch (error) {
      setMessage('Error during signup');
    }
  };

  return (
    <div className="auth-container">
      {registrationSuccess ? (
        <>
          <p>Registration successful! Click below to log in:</p>
          <button type="button" onClick={() => window.location.href = '/login'}>
            Login
          </button>
        </>
      ) : (
        <>
          <h2>Signup</h2>
          <form>
            <div className="input-group">
              <label>Username</label>
              <input
                type="text"
                placeholder="Enter your username"
                value={username}
                onChange={(e) => {
                  setUsername(e.target.value);
                  validateUsername();
                }}
                required
              />
              <p className="error-message">{usernameError}</p>
            </div>
            <div className="input-group">
              <label>Email</label>
              <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => {
                  setEmail(e.target.value);
                  validateEmail();
                }}
                required
              />
              <p className="error-message">{emailError}</p>
            </div>
            <div className="input-group">
              <label>Password</label>
              <input
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => {
                  setPassword(e.target.value);
                  validatePassword();
                }}
                required
              />
              <p className="error-message">{passwordError}</p>
            </div>
            <div className="input-group">
              <label>Confirm Password</label>
              <input
                type="password"
                placeholder="Confirm your password"
                value={confirmPassword}
                onChange={(e) => {
                  setConfirmPassword(e.target.value);
                  validateConfirmPassword();
                }}
                required
              />
              <p className="error-message">{confirmPasswordError}</p>
            </div>
            <button type="button" onClick={handleSignup}>
              Signup
            </button>
            <p className="error-message">{message}</p>
          </form>
        </>
      )}
    </div>
  );
};

export default Signup;
